memberdef.initializer:
= [](float time, float begin, float change,
                         float duration) -> float {
            if (time == 0)
                return begin;
            if ((time /= duration) == 1)
                return begin + change;
            float const p{duration * .3f};
            float const s{p / 4};
            time -= 1;
            return -(change * powf(2, 10 * time) *
                     sinf((time * duration - s) * (2 * PI) / p)) +
                   begin;
        }

= [](float time, float begin, float change, float duration) -> float {
            if (time == 0)
                return begin;
            if ((time /= duration) == 1)
                return begin + change;
            float const p{duration * .3f};
            float const s{p / 4};
            return (change * powf(2, -10 * time) *
                        sinf((time * duration - s) * (2 * PI) / p) +
                    change + begin);
        }

= [](float time, float begin, float change, float duration) -> float {
            if (time == 0)
                return begin;
            if ((time /= duration / 2) == 2)
                return begin + change;
            float const p{duration * (.3f * 1.5f)};
            float const s{p / 4.f};
            if (time < 1)
            {
                time -= 1;
                return -.5f * (change * powf(2, 10 * time) *
                               sinf((time * duration - s) * (2 * PI) / p)) +
                       begin;
            }
            time -= 1;
            return change * powf(2, -10 * time) *
                       sinf((time * duration - s) * (2 * PI) / p) * .5f +
                   change + begin;
        }
